import React, { useEffect, useRef } from "react";
import { GoogleMap, LoadScript } from "@react-google-maps/api";

const NearbySearch = ({ midpoint }) => {
  const mapRef = useRef(null);

  const mapContainerStyle = {
    width: "100vw",
    height: "100vh",
  };

  useEffect(() => {
    if (!midpoint) return;

    const handleLoad = (map) => {
      mapRef.current = map;

      const service = new window.google.maps.places.PlacesService(map);
      const request = {
        location: midpoint,
        radius: 5000, // Search within 5km
        type: ["tourist_attraction", "park", "museum", "library"], // Public places only
      };

      service.nearbySearch(request, (results, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          results.forEach((place) => {
            new window.google.maps.Marker({
              position: place.geometry.location,
              map: map,
            });
          });
        }
      });
    };

    if (mapRef.current) {
      handleLoad(mapRef.current);
    }
  }, [midpoint]);

  return (
    <LoadScript googleMapsApiKey="AIzaSyA2oxgEXddn3TiBypWPBckx0m6iwn5UDyA" libraries={["places"]}>
      <GoogleMap 
        mapContainerStyle={mapContainerStyle} 
        center={midpoint} 
        zoom={14} 
        onLoad={(map) => handleLoad(map)} 
      />
    </LoadScript>
  );
};

export default NearbySearch;
