import React, { useState, useEffect, useRef } from "react";
import { GoogleMap, LoadScript } from "@react-google-maps/api";

const NearbySearch = ({ midpoint }) => {
  const mapRef = useRef(null);
  const [mapCenter, setMapCenter] = useState(midpoint);
  const [places, setPlaces] = useState([]); // ✅ Store places data
  const [searchType, setSearchType] = useState("restaurant"); // Default search type

  const mapContainerStyle = {
    width: "100%",
    height: "500px",
  };

  useEffect(() => {
    if (midpoint) {
      setMapCenter(midpoint);
    }
  }, [midpoint]);

  const handleLoad = (map) => {
    mapRef.current = map;
    searchNearbyPlaces(map);
  };

  const searchNearbyPlaces = (map) => {
    if (!midpoint) {
      console.error("Midpoint not available for search.");
      return;
    }

    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location: midpoint, // ✅ Uses dynamic midpoint
      radius: 5000, // Search within 5 km
      type: searchType, // ✅ Dynamic search type
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        setPlaces(results); // ✅ Store places in state
        results.forEach((place) => {
          new window.google.maps.Marker({
            position: place.geometry.location,
            map: map,
            title: place.name, // ✅ Adds a title
          });
        });
      } else {
        console.error("No places found or error occurred:", status);
      }
    });
  };

  return (
    <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY" libraries={["places"]}>
      <div>
        <label>Search for: </label>
        <select value={searchType} onChange={(e) => setSearchType(e.target.value)}>
          <option value="restaurant">Restaurants</option>
          <option value="hotel">Hotels</option>
          <option value="gas_station">Gas Stations</option>
          <option value="cafe">Cafes</option>
          <option value="supermarket">Supermarkets</option>
        </select>
        <button onClick={() => searchNearbyPlaces(mapRef.current)}>Search</button> {/* ✅ Button to trigger search */}
      </div>

      <GoogleMap mapContainerStyle={mapContainerStyle} center={mapCenter} zoom={14} onLoad={handleLoad} />

      {/* ✅ Display found places */}
      <div>
        <h3>Nearby Places</h3>
        <ul>
          {places.length > 0 ? (
            places.map((place, index) => (
              <li key={index}>
                {place.name} - ⭐ {place.rating || "No Rating"}
              </li>
            ))
          ) : (
            <p>No places found.</p>
          )}
        </ul>
      </div>
    </LoadScript>
  );
};

export default NearbySearch;
