import React, { useState, useEffect, useRef } from "react";
import { GoogleMap, LoadScript } from "@react-google-maps/api";

const NearbySearch = ({ midpoint }) => {
  const mapRef = useRef(null);
  const mapContainerStyle = {
    width: "100vw",
    height: "100vh",
  };

  const [mapCenter, setMapCenter] = useState(midpoint);
  const [places, setPlaces] = useState([]); // Store public places data

  useEffect(() => {
    if (midpoint) {
      setMapCenter(midpoint);
    }
  }, [midpoint]);

  const handleLoad = (map) => {
    mapRef.current = map;

    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location: midpoint,
      radius: 5000, // Search within 5km radius
      type: ["tourist_attraction", "park", "museum", "library"], // Public places
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
        setPlaces(results); // Store places for display
        results.forEach((place) => {
          new window.google.maps.Marker({
            position: place.geometry.location,
            map: map,
            title: place.name,
          });
        });
      } else {
        console.error("No public places found or error occurred:", status);
      }
    });
  };

  return (
    <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY" libraries={["places"]}>
      <GoogleMap mapContainerStyle={mapContainerStyle} center={mapCenter} zoom={14} onLoad={handleLoad} />

      {/* Display Public Places Found */}
      <div style={{ background: "#fff", padding: "10px", borderRadius: "8px", marginTop: "10px" }}>
        <h3>Nearby Public Places</h3>
        <ul>
          {places.length > 0 ? (
            places.map((place, index) => (
              <li key={index}>
                {place.name} - ‚≠ê {place.rating || "No Rating"}
              </li>
            ))
          ) : (
            <p>No public places found.</p>
          )}
        </ul>
      </div>
    </LoadScript>
  );
};

export default NearbySearch;
